import React, {useState} from "react";
import "./Tag.css"; // Import the CSS for styling

const Tag = ({label, currColor}) => { 

    const style = {
        '--primary-color': currColor,
        '--font-color': (currColor && currColor !== 'black') ? 'white' : 'black'
    };

    return (
        <div style={style} className="tag">
            {label}
        </div>
        ); 
    };

export default Tag;
