import React, {useState} from "react";
import "./Menu.css"; // Import the CSS for styling

const Menu = () => {
    /* handle search functions */
    const [searchValue, setSearchValue] = useState(""); 

    const handleInput = (event) => {
        setSearchValue(event.target.value);
    }
    const handleSearch = () => { 
        console.log("Search value: " + searchValue);          
        /* dummy search post function -> posts to a local server running @ port 31337 (NOT WORKING RN BUT NOT VERY IMPORTANT)*/
        fetch('http://localhost:31337', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/text',
            },
            body: { search: searchValue },
        })
        .then(response => response.text())
        .then(data => {
            console.log(data);
        })
        .catch((error) => console.error('Error:', error));

        setSearchValue(''); // Reset search input after receiving the response


    }
    const handleKeyDown = (event) => {
        if (event.key === "Enter") {
            handleSearch(); 
        }
    }

    const handleLogin = () => {
        alert("You will now be redirected to a dummy login page");
        window.location.href= '/login.html'
    }
    return (
    <div className="menu-container">
      <div className="search-bar">
        <input type="text" placeholder="Search topics, bugs, reports, and more" value={searchValue} onChange={handleInput} onKeyDown={handleKeyDown} />
        <button className="search-button" onClick={handleSearch}>âŒ•</button>
      </div>
      <div className="nav-items">
        <a href="#topics">Topics</a>
        <a href="#features">Popular</a>
      </div>
      <div className="user-actions">
        <button className="login-button" onClick={handleLogin}>Login</button>
        <div className="user-icon">
          <a href="#settings">
          <img
            className="user-pic"
            action = "https://google.com/"
            src="https://www.freeiconspng.com/thumbs/profile-icon-png/profile-icon-9.png"></img>
            </a>
        </div>
      </div>
    </div>
    );
};

export default Menu;
