import React, {useState} from "react";
import "./Menu.css"; // Import the CSS for styling

const Menu = () => {
    /* handle search functions */
    const [searchValue, setSearchValue] = useState(""); 

    const handleInput = (event) => {
        setSearchValue(event.target.value);
    }
    const handleSearch = (event) => { 
        console.log("Search value: " + searchValue); 
        setSearchValue("");

        fetch('/search-endpoint', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ search: searchValue }),
        })
        .then(response => response.json())
        .then(data => {
            console.log(data);
            setSearchValue(''); // Reset search input after receiving the response
        })
        .catch((error) => console.error('Error:', error));

    }
    const handleKeyDown = (event) => {
        if (event.key === "Enter") {
            handleSearch(); 
        }
    }
    return (
    <div className="menu-container">
      <div className="search-bar">
        <input type="text" placeholder="Search topics, bug reports, and more" value={searchValue} onChange={handleInput} onKeyDown={handleKeyDown} />
        <button className="search-button" onClick={handleSearch}>âŒ•</button>
      </div>
      <div className="nav-items">
        <a href="#topics">topics</a>
        <a href="#bug">bugs</a>
        <a href="#features">features</a>
      </div>
      <div className="user-actions">
        <button className="login-button">Login</button>
        <div className="user-icon">
          <a href="#settings">
          <img
            className="user-pic"
            action = "https://google.com/"
            src="https://www.freeiconspng.com/thumbs/profile-icon-png/profile-icon-9.png"></img>
            </a>
        </div>
      </div>
    </div>
    );
};

export default Menu;
